import React from "react";
import styles from "./ExpenseList.module.css";

const ExpenseList = ({deleteExpense, expenses}) => {
  return (
    <div className={styles.expenseListContainer}>
      <h3>Transactions</h3>
      <ul className={styles.transactionList}>

        {
          expenses.map((expense) => (
            <li key={expense.id} className="styles.transactionItem">
              <span>{expense.text}</span>
              <span>{expense.amount < 0 ? '-' : '+'}${Math.abs(expense.amount)}</span>
              <button className="styles.deleteBtn" onClick={() => deleteExpense(expense.id)}>X</button>
            </li>
          ))
        }
      </ul>
    </div>
  );
};

export default ExpenseList;
