import React from "react";
import styles from "./ExpenseInfo.module.css";

const ExpenseInfo = (props) => {
  // Add logic here to calculate the grand total, profit and expense amount here

  const amounts = props.expenses.map(expense => expense.amount);
  const totalBalance = amounts.reduce((acc, item) => acc + item, 0).toFixed(2);
  const income = amounts.filter(amount => amount > 0).reduce((acc, item) => acc + item, 0).toFixed(2);
  const expense = (
    amounts.filter(amount => amount < 0).reduce((acc, item) => acc + item, 0)
  )

  return (
    <div className={styles.expenseInfoContainer}>
      <div className={styles.balance}>
        <h4>YOUR BALANCE</h4>
        <h1>${/* Grand total should be displayed here */}</h1>
      </div>
      <div className={styles.incomeExpenseContainer}>
        <div>
          <h4>Income</h4>
          <p id="money-plus" className={`${styles.money} ${styles.plus}`}>
            +${/*Total Profit Amount should be displayed here */}
          </p>
        </div>
        <div>
          <h4>Expense</h4>
          <p id="money-minus" className={`${styles.money} ${styles.minus}`}>
            -${/* Total expense amount should be displayed here */}
          </p>
        </div>
      </div>
    </div>
  );
};

export default ExpenseInfo;
